import React, { useState, useEffect } from 'react';
import VisitsTitle from 'components/VisitsTitle';
import VisitsTable from 'components/VisitsTable';
import axios from 'axios';

export default function Visits() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [visitors, setVisitors] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const config = {
      headers: { Authorization: `Bearer ${token}` }
    };
    axios.get('http://127.0.0.1:8000/api/visits', config)
      .then(
        (response) => {
          setIsLoaded(true);
          const visitors = response.data['hydra:member'];
          console.log(visitors);
          setVisitors(visitors);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <div className="text-red-500 text-xl font-bold mb-4">
          Error: {error.message}
        </div>
      </div>
    );
  } else if (!isLoaded) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <div className="text-gray-500 text-xl font-bold mb-4">Loading...</div>
      </div>
    );
  } else {
    return (
      <div className="container mx-auto mt-10">
        <VisitsTitle visitorsCount={visitors.length} />
        <VisitsTable visitors={visitors} />
      </div>
    );
  }
}
