  return (
    <div className="bg-blue-600 text-white py-6 px-8 rounded-t-md">
      <h1 className="text-3xl font-bold mb-2">Liste des visiteurs</h1>
      <p className="text-lg">
        {new Date().toLocaleDateString("fr-FR", options)} :{" "}
        {props.visitorsCount === 0 ? "Aucun" : props.visitorsCount} visiteur
        {plural} pr√©sent{plural}
      </p>
    </div>
  );
}
