  useEffect(() => {
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios.get("http://127.0.0.1:8000/api/visits", config).then(
      (response) => {
        setIsLoaded(true);
        const visitors = response.data["hydra:member"];
        console.log(visitors);
        setVisitors(visitors);
      },
      (error) => {
        setIsLoaded(true);
        setError(error);
      }
    );
  }, []);

  if (error) {
    return (
      <>
        <Navbar current="visits" />
        <div className="m-10">
          <div>Error: {error.message}</div>
        </div>
      </>
    );
  } else if (!isLoaded) {
    return (
      <>
        <Navbar current="visits" />
        <div className="m-10 flex justify-center items-center h-screen">
          <div className="text-gray-500 text-xl font-medium animate-pulse">
            Loading...
          </div>
        </div>
      </>
    );
  } else {
    return (
      <>
        <Navbar current="visits" />
        <div className="m-10">
          <VisitsTitle visitorsCount={visitors.filter((v)=>v.leavingDate===undefined).length} />
          <VisitsTable visitors={visitors} />
        </div>
      </>
    );
  }
}
