import React from "react";
import dayjs from "dayjs";
import axios from "axios";

export default function VisitsTableData(props) {
  return (
    <table className="table-auto w-full">
      <thead>
        <tr className="bg-gray-200 text-gray-700">
          <th className="px-4 py-2">Pr√©nom</th>
          <th className="px-4 py-2">Nom</th>
          <th className="px-4 py-2">Entreprise</th>
          <th className="px-4 py-2">Motif</th>
        </tr>
      </thead>
      <tbody>
        {props.visitors?.map((visitor, index) => (
          <tr key={index}>
            <td className="border px-4 py-2">{visitor.firstname}</td>
            <td className="border px-4 py-2">{visitor.lastname}</td>
            <td className="border px-4 py-2">{visitor.company}</td>
            <td className="border px-4 py-2">{visitor.reason.reasonName}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
  function handleClick(e) {
    e.preventDefault();
    console.log(props.visitor);
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    const newVisit = {
      leavingDate: dayjs(),
    };
    console.log(newVisit);
    axios
      .put(`http://127.0.0.1:8000/api/visits/${props.visitor.id}`, newVisit)
      .then((response) => {
        console.log(response);
        window.location.reload();
      })
  }
}
