import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "components/Navbar";
import jwtDecode from "jwt-decode";

export default function Employees() {
  const token = localStorage.getItem("token");
  if (!token) {
    window.location.href = "/login";
  }
  if (!jwtDecode(token).roles.includes("ROLE_ADMIN")) {
    window.location.href = "/visits";
  }

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [employees, setEmployees] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };
    axios.get("http://127.0.0.1:8000/api/employees", config).then(
      (response) => {
        setIsLoaded(true);
        const employees = response.data["hydra:member"];
        console.log(employees);
        setEmployees(employees);
      },
      (error) => {
        setIsLoaded(true);
        setError(error);
      }
    );
  }, []);

  function handleSubmit(event) {
    event.preventDefault();
    console.log(event.target);
  }

  return (
    <>
      <Navbar current="employees" />
      <div className="m-10 grid grid-cols-1 sm:grid-cols-2 gap-10">
        <form className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
          <h1 className="text-2xl font-semibold mb-6">Saisie d'un nouvel employé :</h1>
            <div>
              <label
                htmlFor="first-name"
                className="block text-sm font-medium leading-5 text-gray-700"
              >
                Prénom
              </label>
              <div className="mt-1 relative rounded-md shadow-sm">
                <input
                  type="text"
                  name="first-name"
                  id="first-name"
                  autoComplete="given-name"
                  className="block w-full pr-10 form-input sm:text-sm sm:leading-5"
                  placeholder="Entrez le prénom"
                />
              </div>
            </div>

            <div>
              <label
                htmlFor="last-name"
                className="block text-sm font-medium leading-5 text-gray-700"
              >
                Nom
              </label>
              <div className="mt-1 relative rounded-md shadow-sm">
                <input
                  type="text"
                  name="last-name"
                  id="last-name"
                  autoComplete="family-name"
                  className="block w-full pr-10 form-input sm:text-sm sm:leading-5"
                  placeholder="Entrez le nom"
                />
              </div>
            </div>
          <div className="flex items-center justify-center">
            <button
              type="submit"
              onClick={handleSubmit}
              className="bg-indigo-500 hover:bg-indigo-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Enregistrer
            </button>
          </div>
        </form>
      </div>
    </>
  );
}
